<template>
  <view class="avatar">
    <image style="width: 50px; height: 50px;" :src="avatar" />
  </view>
</template>
<script setup lang="ts">
  import {reactive, computed, defineProps} from 'vue'

  const props = defineProps({
    src: {
      type: String,
      default: ''
    }
  })

  const state = reactive({
    defaultUrl: 'data:image/png;base64,'
  })
  const avatar = computed(() => props.src || state.defaultUrl)
</script>
